import React, { useState } from 'react';

const Details=(props)=>{
    return (
      
        <div style={{margin:100,marginTop:0}}>
        <img src="/back.png" style={{width:30,height:30}} /*onClick={this.onClick}*/ /> <br/><br/>
       {/* <button onClick={this.onClick}> back</button> */}
      <div style={{position:'relative', width:'750px',height:600}}>
       <div style={{width:'100%',left:'25%',top:'25%'}}>
          <img src={props.one.image} style={{width:250 ,height:220 , float:'right'}}/> 
        
      
      <div style={{position:'absolute', overflowY:'auto',width:'100%',height:'100%'}}>
       
        <h2 style={{height:70,width:500,textAlign:'center'}}>{props.one.title}</h2>
        <h3 style={{textAlign:'center',height:20,width:500}}>{props.one.price}</h3> 
        <h3 style={{height:40,width:500,textAlign:'center'}}>{props.one.category}</h3> <br/>
        <p style={{height:40,width:400,textAlign:'center'}}>{props.one.description}</p>
      </div> 
      </div> </div>

</div>
    )
}
export default Details ;
