import React, { Component } from "react";
import {  BrowserRouter as Router, Route,Link, Switch  } from "react-router-dom";
import Products from './Products'
import OneProduct from './OneProduct'
import { FaSearch} from "react-icons/fa";
import { BlockList } from "net";
class App extends Component {

  state={
    username :"",
    password:"",
    flagUserPass: true,
    flagLogin:true,
    products:[],
    flagProduct:false,
    jewelery:[],
    electronics:[],
    menClothing:[],
    womenClothing:[]

  }
  componentDidMount(){
    this.setState({username:'',password:'',flagUserPass:true,flagLogin:true,flagProduct:false})
    fetch('https://fakestoreapi.com/products')
           .then(res=>res.json())
           .then(json=>this.setState({products:json}))

    //console.log(" products "+this.state.products)
    fetch('https://fakestoreapi.com/products/category/jewelery')
            .then(res=>res.json())
            .then(json=>this.setState({jewelery:json}))

    //console.log("jewelery "+this.state.jewelery)
    fetch('https://fakestoreapi.com/products/category/electronics')
            .then(res=>res.json())
            .then(json=>this.setState({electronics:json}))

    //console.log("electronics "+this.state.electronics)
    fetch("https://fakestoreapi.com/products/category/men's clothing")
            .then(res=>res.json())
            .then(json=>this.setState({ menClothing :json}))

    //console.log("menClothing"+this.state.menClothing)
     fetch("https://fakestoreapi.com/products/category/women's clothing")    
            .then(res=>res.json())
            .then(json=>this.setState({ womenClothing :json}))



  }

 login=()=> {


  if(this.state.username.length>0&&this.state.password.length>0){
  let result=  fetch('https://fakestoreapi.com/auth/login',{
            method:'POST',
            headers:{
              "Content-Type":"application/json",
              "Accept" :"application/json"
            },
            body:JSON.stringify({
                username: this.state.username,
                password: this.state.password
            })
        })  .then(res=>res.json())
        .then(json=>{
          if(json.status=="Error"){
           this.setState({flagLogin:false,flagUserPass:true});
          console.log("Error");

           } else{
            this.setState({flagLogin:true,flagUserPass:true,flagProduct:true});

         }


         } )
      }
      else{
        this.setState({flagUserPass:false})
      }


          }






  render () {return (

    <div style={{background:'#D3D3D3',width:'100%',height:'100%',position:'absolute', top:'0px', right:'0px',  bottom:'0px', left:'0px'}}>
      {(this.state.flagProduct==false)?
    <div style={{ background:'#FFFFFF' ,border: '2px solid ' ,marginTop:100, width:500,height:600,marginLeft:500}} >


        <br/> <br/>
          <label style={{ marginLeft:150, fontSize:40 , fontFamily: "Times New Roman"}} >Login Page</label>
        <br/> <br/><br/> <br/><br/> <br/>

           <input style={{ marginTop:10, marginLeft:100 , width: '350px',  border: '2px solid ' ,height:30   }} 
             placeholder="User Name"
             onChange = {(e) => this.setState({username:e.target.value})}
             />
   <br/> <br/>
             <input  style={{ marginTop:10, marginLeft:100 , width: '350px',  border: '2px solid ' ,height:30     }} 
               type="password"
               placeholder=" password"
               onChange = {(e) => this.setState({password:e.target.value})}
               />
             <br/><br/>  <br/> <br/>  <br/> <br/><br/> <br/>


             <button    onClick={this.login} style={{ height:50, marginTop:10, marginLeft:150, width: '250px',  border: '2px solid ',background:'#D3D3D3'}} >
        Login </button>
      <div style={{color:'red ',marginLeft:10}}> {(this.state.flagUserPass==true)?"":"username or password is empty"} </div> 
      <div style={{color:'red ',marginLeft:10}}> {(this.state.flagLogin==true)?"":"username or password is incorrect"} </div> 
       </div>   
      : <div style={{background:"FFFFFF",position:'absolute', top:'0px', right:'0px',  bottom:'0px', left:'0px'}}>

         <Router>
      <div>
        <div>
            <Link to="/">All</Link>
         |
            <Link to="/Categpry1"> menClothing </Link>
         |
            <Link to="/Categpry2"> womenClothing </Link>
         |
            <Link to="/Categpry3"> Categpry3 </Link>
         |           
            <Link to="/Categpry4"> Categpry4 </Link>
<br/>
            <FaSearch style={{border: 'none', border: 0,background:'#fff', height:20,width:30}} />
            <input type="text" name="title" placeholder = "search by product name" style={{border: 'none',border: 0,background:'#fff',flex: '10', 
             padding: '5px',  marginLeft:'auto', width: 300,  height:30}}  />
         </div>
            <div >
        <Switch>

          <Route exact path="/">
          <Products products={this.state.products}   />
          </Route>

          <Route exact path="/Categpry1">
          <Products products={this.state.menClothing}   />
          </Route>

          <Route exact path="/Categpry2">
          <Products products={this.state.womenClothing}   />
          </Route>





        </Switch>
      </div>

  {/*  */}
 </div>

 </Router>                 
<br/>



        </div>
        }


    </div>
  );

}

}

export default App;
