import React, { useState } from 'react';
const All=(props)=>{
let obj=[]
    const [products, setproducts] = useState([]);
    fetch('https://fakestoreapi.com/products')
    .then(res=>res.json())
    .then(json=>setproducts(json))
    
    const fun=()=>{
if(props.search!=""){
    products.filter((val)=>{
    
      if(val.title?.toLowerCase().includes(props.search?.toLowerCase())){
       obj.push(val)
  
        
      }
    })
   if(obj.length==0) setproducts([])
   else setproducts(obj)
  }
 
}
    return( (props.search=="" || obj.length!=0)?
        <div>{
          

            products.map((oneproduct) =>(
         <div className="oneProduct" key={oneproduct.id}>
             <div  >
                           <img src={oneproduct.image} className="imagestyle"/><br/>
                           <div className="titlestyle">{oneproduct.title}</div>
                           <div className="pricestyle">{oneproduct.price}</div>
                          </div>
          </div>
             ))
             }</div>:
             (obj==[]&&props.search!="")?
             <div style={{fontFamily:'arial',fontSize:'25px',color:'red'}}>This item does not exist</div>:<></>
             )
}
export default All;
