import React, { useState } from 'react';
const All=(props)=>{
let obj=[];
    const [products, setproducts] = useState([]);
    fetch('https://fakestoreapi.com/products')
    .then(res=>res.json())
    .then(json=>setproducts(json))
    
    
    products.filter((val)=>{
      
    if(val.title?.toLowerCase().includes(props.search?.toLowerCase())&&props.search!=0){
       obj.push(val) }
    })
   
//  console.log(obj.length)
 

   if(props.search==''){ return( 
        <div>
          {
      // console.log('props.search== '),
            products.map((oneproduct) =>(
                
          
         <div className="oneProduct" key={oneproduct.id}>
             <div  >
                           <img src={oneproduct.image} className="imagestyle"/><br/>
                           <div className="titlestyle">{oneproduct.title}</div>
                           <div className="pricestyle">{oneproduct.price}</div>
                          </div>
          </div>
             ))
             }</div>)}
             else if(obj.length!=0&&props.search!='')
             {return(
        <div>{
         // console.log('obj!=[]&&props.search!='),

            obj.map((oneproduct) =>(
         <div className="oneProduct" key={oneproduct.id}>
             <div  >
                           <img src={oneproduct.image} className="imagestyle"/><br/>
                           <div className="titlestyle">{oneproduct.title}</div>
                           <div className="pricestyle">{oneproduct.price}</div>
                          </div>
          </div>
             ))
             }</div>)}
             else if(obj.length==0&&props.search!='') {return(
             <div style={styleNot}>This product does not exist</div>
             )}
}
const styleNot={
 fontFamily:'arial',
 fontSize:'5vh',
 color:'red',
 marginTop:"5vh"

}
export default All;
