import React, { useState } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';
import Products from './Products';

import './App.css'
const Login= () =>{
  
  const [username, setusername] = useState('');
  const [pass, setpass] = useState('');
  const[flagLogin,setflag]=useState(false);
  const submit=()=>{
    
   
      let result=  fetch('https://fakestoreapi.com/auth/login',{
                method:'POST',
                headers:{
                  "Content-Type":"application/json",
                  "Accept" :"application/json"
                },
                body:JSON.stringify({
                    username: username,
                    password: pass
                })
            })  .then(res=>res.json())
            .then(json=>{
              if(json.status=="Error"){
                console.log("false");
                setflag(false);
    
               } else{
                setflag(true);
               
             }
              
              
             } )
         
         
          
    
    
              }
    return (
     
        (!flagLogin)?
        <MuiThemeProvider >
           <div className="container">
       <div className="child"><br/>
       <label className="loginString" >Login Page</label>
           <TextField 
             hintText=" Username"
             style={styleText}
             onChange = {(event,newValue) => setusername(newValue)}
             />
           <br/> <br/>
             <TextField
               type="password"
               hintText="Password"
              style={styleText}
              onChange = {(event,newValue) => setpass(newValue)}
               />
             <br/>
            
             {(username.length>0 &&pass.length>0)?
             <RaisedButton label="login"  style={loginString} onClick={(even,newValue)=>submit()}
              />:<></>}
         
              
               </div>
               </div>
         </MuiThemeProvider>:
         Products(username)
     
    );
  }

const styleText={
  outlineStyle: 'solid',
  marginLeft:'15%',
  marginTop:'8%',
  width:'80%',
  fontSize:'2vh'
}

const loginString={
  outlineStyle: 'solid',
  marginLeft:'15%',
  marginTop:'50%',
  width:'80%',
  fontSize:'3vh',
 
}

export default Login;
